package com.chang.leetcode.contest.weekly150;

import java.math.BigDecimal;
import java.math.RoundingMode;

public class Problem1163 {

    // Time Limit Exceed
    public String lastSubstringTooSlow(String s) {
        int startPos = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) > s.charAt(startPos)) {
                startPos = i;
            } else if (s.charAt(i) == s.charAt(startPos)) {
                if (s.substring(i).compareTo(s.substring(startPos)) > 0) {
                    startPos = i;
                }
            }
        }
        return s.substring(startPos);
    }

    public static void main(String[] args) {
        Problem1163 problem = new Problem1163();
        System.out.println("bab".equals(problem.lastSubstring("abab")));
        System.out.println("tcode".equals(problem.lastSubstring("leetcode")));
        System.out.println("zzwobllyxktqeibfoupcpptncggrdqbkji".equals(problem.lastSubstring("xbylisvborylklftlkcioajuxwdhahdgezvyjbgaznzayfwsaumeccpfwamfzmkinezzwobllyxktqeibfoupcpptncggrdqbkji")));

        final String a = "";
        System.out.println(a.equals(problem.lastSubstring(a)));
    }

    // example 1, not good enough, just in order to past test case of "aaaaaaaaaaaaaaaaaaaaa..."
    public String lastSubstringExample(String s) {
        int largestCharacter = s.charAt(0);
        boolean allCharsSame = true;
        for (char c : s.toCharArray()) {
            if (c != largestCharacter)
                allCharsSame = false;
            largestCharacter = Math.max(largestCharacter, c);
        }
        if (allCharsSame)
            return s;
        String result = "";
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == largestCharacter && s.substring(i).compareTo(result) > 0)
                result = s.substring(i);
        }
        return result;
    }

    // awesome
    // transfer substring to a double(with the highest char represents integer.)
    public String lastSubstring(String S) {
        int len = 100000;
        BigDecimal mx = BigDecimal.ZERO, pre = BigDecimal.ZERO;
        int idx = 0, n = S.length();
        for (int i = n - 1; i >= 0; --i) {
            BigDecimal cur = pre.divide(new BigDecimal("26"), len, RoundingMode.HALF_DOWN).add(new BigDecimal(S.charAt(i) - 'a'));
            if (cur.compareTo(mx) > 0) {
                mx = cur;
                idx = i;
            }
            pre = cur;
        }
        return S.substring(idx);
    }

}
